import React, { useState } from "react";
import axios from "axios";
import { Form, Button, Alert } from "react-bootstrap";

const AvailabilityForm = () => {
  const [formData, setFormData] = useState({
    location: "",
    vehicleType: "",
    startDateTime: "",
    durationMins: ""
  });
  const [availabilityResult, setAvailabilityResult] = useState(null);
  const [error, setError] = useState(null);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const checkAvailability = async () => {
    try {
      const response = await axios.get("http://localhost:8000/availability", {
        params: formData
      });
      setAvailabilityResult(response.data);
      setError(null);
    } catch (err) {
      setError(err.response ? err.response.data.message : "Error occurred");
      setAvailabilityResult(null);
    }
  };

  return (
    <div>
      <h2>Check Vehicle Availability</h2>
      {error && <Alert variant="danger">{error}</Alert>}
      <Form>
        <Form.Group controlId="location">
          <Form.Label>Location</Form.Label>
          <Form.Control
            type="text"
            name="location"
            value={formData.location}
            onChange={handleChange}
          />
        </Form.Group>
        <Form.Group controlId="vehicleType">
          <Form.Label>Vehicle Type</Form.Label>
          <Form.Control
            type="text"
            name="vehicleType"
            value={formData.vehicleType}
            onChange={handleChange}
          />
        </Form.Group>
        <Form.Group controlId="startDateTime">
          <Form.Label>Start DateTime</Form.Label>
          <Form.Control
            type="datetime-local"
            name="startDateTime"
            value={formData.startDateTime}
            onChange={handleChange}
          />
        </Form.Group>
        <Form.Group controlId="durationMins">
          <Form.Label>Duration (Minutes)</Form.Label>
          <Form.Control
            type="number"
            name="durationMins"
            value={formData.durationMins}
            onChange={handleChange}
          />
        </Form.Group>
        <Button className="mt-3" onClick={checkAvailability}>
          Check Availability
        </Button>
      </Form>

      {availabilityResult && (
        <Alert variant="success" className="mt-3">
          Vehicle available with ID: {availabilityResult.vehicleId}
        </Alert>
      )}
    </div>
  );
};

export default AvailabilityForm;
