import React, { useState } from 'react';
import { scheduleTestDrive } from '../api';

export default function ScheduleForm() {
    const [vehicleId, setVehicleId] = useState('');
    const [startDateTime, setStartDateTime] = useState('');
    const [durationMins, setDurationMins] = useState('');
    const [customerName, setCustomerName] = useState('');
    const [customerPhone, setCustomerPhone] = useState('');
    const [customerEmail, setCustomerEmail] = useState('');
    const [message, setMessage] = useState('');

    const handleScheduleTestDrive = async () => {
        try {
            const bookingData = {
                vehicleId,
                startDateTime,
                durationMins,
                customerName,
                customerPhone,
                customerEmail,
            };
            const result = await scheduleTestDrive(bookingData);
            setMessage(result.message || 'Test drive scheduled successfully!');
        } catch (error) {
            setMessage('Error scheduling test drive.');
        }
    };

    return (
        <div>
            <h2>Schedule a Test Drive</h2>
            <div>
                <label>Vehicle ID:</label>
                <input type="text" value={vehicleId} onChange={(e) => setVehicleId(e.target.value)} />
            </div>
            <div>
                <label>Start Date and Time:</label>
                <input type="datetime-local" value={startDateTime} onChange={(e) => setStartDateTime(e.target.value)} />
            </div>
            <div>
                <label>Duration (minutes):</label>
                <input type="number" value={durationMins} onChange={(e) => setDurationMins(e.target.value)} />
            </div>
            <div>
                <label>Customer Name:</label>
                <input type="text" value={customerName} onChange={(e) => setCustomerName(e.target.value)} />
            </div>
            <div>
                <label>Customer Phone:</label>
                <input type="tel" value={customerPhone} onChange={(e) => setCustomerPhone(e.target.value)} />
            </div>
            <div>
                <label>Customer Email:</label>
                <input type="email" value={customerEmail} onChange={(e) => setCustomerEmail(e.target.value)} />
            </div>
            <button onClick={handleScheduleTestDrive}>Schedule Test Drive</button>
            {message && <p>{message}</p>}
        </div>
    );
}
