import React, { useState } from "react";
import axios from "axios";
import { Form, Button, Alert } from "react-bootstrap";

const AddVehicleForm = () => {
  const [formData, setFormData] = useState({
    id: "",
    vehicleType: "",
    location: "",
    startDateTime: "",
    durationMins: ""
  });

  const [responseMessage, setResponseMessage] = useState(null);
  const [error, setError] = useState(null);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(
        "http://localhost:8000/api/addVehicle",
        formData
      );
      setResponseMessage(response.data.message);
      setError(null);
    } catch (err) {
      setError(err.response ? err.response.data.message : "Error occurred");
      setResponseMessage(null);
    }
  };

  return (
    <div>
      <h2>Add a New Vehicle</h2>
      {error && <Alert variant="danger">{error}</Alert>}
      {responseMessage && <Alert variant="success">{responseMessage}</Alert>}
      <Form onSubmit={handleSubmit}>
        <Form.Group controlId="id">
          <Form.Label>Vehicle ID</Form.Label>
          <Form.Control
            type="text"
            name="id"
            value={formData.id}
            onChange={handleChange}
            required
          />
        </Form.Group>
        <Form.Group controlId="vehicleType">
          <Form.Label>Vehicle Type</Form.Label>
          <Form.Control
            type="text"
            name="vehicleType"
            value={formData.vehicleType}
            onChange={handleChange}
            required
          />
        </Form.Group>
        <Form.Group controlId="location">
          <Form.Label>Location</Form.Label>
          <Form.Control
            type="text"
            name="location"
            value={formData.location}
            onChange={handleChange}
            required
          />
        </Form.Group>
        <Form.Group controlId="startDateTime">
          <Form.Label>Start DateTime</Form.Label>
          <Form.Control
            type="datetime-local"
            name="startDateTime"
            value={formData.startDateTime}
            onChange={handleChange}
            required
          />
        </Form.Group>
        <Form.Group controlId="durationMins">
          <Form.Label>Duration (Minutes)</Form.Label>
          <Form.Control
            type="number"
            name="durationMins"
            value={formData.durationMins}
            onChange={handleChange}
            required
          />
        </Form.Group>
        <Button type="submit" className="mt-3">
          Add Vehicle
        </Button>
      </Form>
    </div>
  );
};

export default AddVehicleForm;
