import React, { useState } from "react";
import axios from "axios";
import { Form, Button, Alert } from "react-bootstrap";

const ScheduleForm = () => {
  const [formData, setFormData] = useState({
    vehicleId: "",
    startDateTime: "",
    durationMins: "",
    customerName: "",
    customerPhone: "",
    customerEmail: ""
  });
  const [scheduleResult, setScheduleResult] = useState(null);
  const [error, setError] = useState(null);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const scheduleTestDrive = async () => {
    try {
      const response = await axios.post(
        "http://localhost:8000/schedule",
        formData
      );
      setScheduleResult(response.data);
      setError(null);
    } catch (err) {
      setError(err.response ? err.response.data.message : "Error occurred");
      setScheduleResult(null);
    }
  };

  return (
    <div>
      <h2>Schedule a Test Drive</h2>
      {error && <Alert variant="danger">{error}</Alert>}
      <Form>
        <Form.Group controlId="vehicleId">
          <Form.Label>Vehicle ID</Form.Label>
          <Form.Control
            type="text"
            name="vehicleId"
            value={formData.vehicleId}
            onChange={handleChange}
          />
        </Form.Group>
        <Form.Group controlId="startDateTime">
          <Form.Label>Start DateTime</Form.Label>
          <Form.Control
            type="datetime-local"
            name="startDateTime"
            value={formData.startDateTime}
            onChange={handleChange}
          />
        </Form.Group>
        <Form.Group controlId="durationMins">
          <Form.Label>Duration (Minutes)</Form.Label>
          <Form.Control
            type="number"
            name="durationMins"
            value={formData.durationMins}
            onChange={handleChange}
          />
        </Form.Group>
        <Form.Group controlId="customerName">
          <Form.Label>Customer Name</Form.Label>
          <Form.Control
            type="text"
            name="customerName"
            value={formData.customerName}
            onChange={handleChange}
          />
        </Form.Group>
        <Form.Group controlId="customerPhone">
          <Form.Label>Customer Phone</Form.Label>
          <Form.Control
            type="tel"
            name="customerPhone"
            value={formData.customerPhone}
            onChange={handleChange}
          />
        </Form.Group>
        <Form.Group controlId="customerEmail">
          <Form.Label>Customer Email</Form.Label>
          <Form.Control
            type="email"
            name="customerEmail"
            value={formData.customerEmail}
            onChange={handleChange}
          />
        </Form.Group>
        <Button className="mt-3" onClick={scheduleTestDrive}>
          Schedule Test Drive
        </Button>
      </Form>

      {scheduleResult && (
        <Alert variant="success" className="mt-3">
          Test Drive scheduled successfully for {scheduleResult.customerName}!
        </Alert>
      )}
    </div>
  );
};

export default ScheduleForm;
